INSERT INTO `setting` VALUES ('1', 'CÔNG TY CỔ PHẦN UTICO', 'CÔNG TY CỔ PHẦN UTICO', 'www.visci.org', 'https://www.facebook.com', 'https://maps.google.com/maps?q=s%E1%BB%91%201%20nguy%E1%BB%85n%20huy%20t%C6%B0%E1%BB%9Fng&t=&z=17&ie=UTF8&iwloc=&output=embed', 'support@visci.org', '+ 84 (0) 1 234 56 78', '+ 84 (0) 1 234 56 78', 'Số 31, Ngách 27, Ngõ 16 Huỳnh Thúc Kháng, Láng Hạ, Đống Đa, Hà Nội', null, '/backend/uploads/images/logo.png', '[{\"image\":\"\\/frontend\\/images\\/logo-w-university2x.png\",\"link\":\"https:\\/\\/www.washington.edu\"},{\"image\":\"\\/frontend\\/images\\/logo-nasa2x.png\",\"link\":\"https:\\/\\/www.nasa.gov\"},{\"image\":\"\\/frontend\\/images\\/logo-adpc2x.png\",\"link\":\"http:\\/\\/www.adpc.net\\/\"},{\"image\":\"\\/frontend\\/images\\/logo-nawapi2x.png\",\"link\":\"https:\\/\\/nawapi.gov.vn\"},{\"image\":\"\\/frontend\\/images\\/logo-sevirmekong2x.png\",\"link\":\"https:\\/\\/servir.adpc.net\"},{\"image\":\"\\/frontend\\/images\\/logo-usaid2x.png\",\"link\":\"https:\\/\\/www.usaid.gov\"}]', '[{\"question\":\"How is the product is available?\",\"vi_question\":\"L\\u00e0m th\\u1ebf n\\u00e0o l\\u00e0 s\\u1ea3n ph\\u1ea9m c\\u00f3 s\\u1eb5n?\",\"answer\":\"Our products are sold exclusively online on this website. If you experience difficulty in proceeding with a purchase or you have questions about our products, you can contact us using the contact form found in the contact us page. We will answer your query within a few hours.\",\"vi_answer\":\"S\\u1ea3n ph\\u1ea9m c\\u1ee7a ch\\u00fang t\\u00f4i \\u0111\\u01b0\\u1ee3c b\\u00e1n \\u0111\\u1ed9c quy\\u1ec1n tr\\u1ef1c tuy\\u1ebfn tr\\u00ean trang web n\\u00e0y. N\\u1ebfu b\\u1ea1n g\\u1eb7p kh\\u00f3 kh\\u0103n trong vi\\u1ec7c ti\\u1ebfn h\\u00e0nh mua h\\u00e0ng ho\\u1eb7c b\\u1ea1n c\\u00f3 th\\u1eafc m\\u1eafc v\\u1ec1 s\\u1ea3n ph\\u1ea9m c\\u1ee7a ch\\u00fang t\\u00f4i, b\\u1ea1n c\\u00f3 th\\u1ec3 li\\u00ean h\\u1ec7 v\\u1edbi ch\\u00fang t\\u00f4i b\\u1eb1ng c\\u00e1ch s\\u1eed d\\u1